mapper.select:
SELECT *
FROM user
WHERE userEmail = #{userEmail}
  AND userpw = #{userPw}

SELECT userEmail
FROM user
WHERE userName = #{userName}
  AND userSSN = #{userSSN}

SELECT count(*) > 0
FROM user
WHERE userEmail = #{userEmail}
  AND userPhone = #{userPhone}

mapper.update:
UPDATE user
SET userPw = #{newPassword}
WHERE userId = #{userId}

UPDATE user
SET userPw = #{newPassword}
WHERE userId = #{userId}

mapper.select:
SELECT count(*)
FROM user
WHERE userEmail = #{userEmail}

SELECT count(*)
FROM user
WHERE userPhone = #{userPhone}
